    ga.addParam('sysparm_qry', qry);
    ga.addParam('sysparm_limit', limit);
    ga.addParam('sysparm_orderby', order);
    ga.getXML(userResponse);
	*/

}

function userResponse(response) {
	var answer = response.responseXML.documentElement.getAttribute('answer');

	var result = JSON.parse(answer);

	if (result.error !== undefined) alert(result.error);
	g_form.setValue('requestfor_email',(result.email === undefined) ? '' : result.email);  

}
